import React, { Component } from 'react';
import { firebaseDatabase } from '../utils/firebaseUtils';

export default class System extends Component {
    constructor(props) {
        super(props);
        this.state = this.initialState();
    }

    initialState = () => {
        return {
            active: false,
            data: []
        }
    }

    componentDidMount() {
        const accountRef = firebase.database().ref('accounts');
        accountRef.on('value', (snapshot) => {
            let accounts = snapshot.val();
            let newState = [];
            for (let account in accounts) {
                newState.push({
                    id: account,
                    account: accounts[account].account,
                    title: accounts[account].title,

                })
            }
            this.setState({
                accounts: newState,
            })
        })
        const userRef = firebase.database().ref('users');
        userRef.on('value', (snapshot) => {
            let users = snapshot.val();
            let newUserState = [];
            for (let data in users) {
                newUserState.push({
                    id: data,
                    account: users[data].account,
                    name: users[data].name,
                })
                console.log(data)
            }
        })
    }

    turnOn = () => {
        this.setState({active: true})
        firebaseDatabase.ref().update({
            active: true
        })
    }

    turnOff = () => {
        this.setState({ active: false })

        firebaseDatabase.ref().update({
            active: false
        })
    }

    render() {
        let { active } = this.state;
        return (
            <div>
                { 
                    active ? <div>
                        <span>Cafeteira Ligada</span>
                        <button onClick={this.turnOff}>Desligar</button>
                    </div> : <div>
                        <span>Cafeteira Desligada</span>
                        <button onClick={this.turnOn}>Ligar</button>
                    </div>
                }
            </div>
        );
    }
};
