import React, { Component } from 'react';
import { firebaseDatabase } from '../utils/firebaseUtils';
import { makeStyles } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';
import Slider from '@material-ui/core/Slider';

function valuetext(value) {
    firebaseDatabase.ref().update({
        quantity: value
    })
}
const marks = [
    {
        value: 0,
        label: '0'
    },
    {
        value: 200,
        label: '200ml'
    }
]
export default class System extends Component {
    constructor(props) {
        super(props);
        this.state = this.initialState();
    }

    initialState = () => {
        return {
            data: []
        }
    }

    componentDidMount() {
        const dataRef = firebaseDatabase.ref();
        dataRef.on('value', (snapshot) => {
            let data = snapshot.val();
            let newState = [];

            newState.push({
                active: data.active, 
                withCup: data.withCup
            })

            this.setState({
                data: newState,
            })
        })
    }

    turnOn = () => {
        firebaseDatabase.ref().update({
            active: true
        })
    }

    turnOff = () => {
        firebaseDatabase.ref().update({
            active: false
        })
    }

    startPrepare = () => {
        firebaseDatabase.ref().update({
            preparing: true
        })
    }

    render() {
        let { data } = this.state;
        return (
            <div>
                { 
                    data[0] && (data[0].active == true) ? 
                    <div>
                        <div>
                            <span>Cafeteira Ligada</span>
                            <button onClick={this.turnOff}>Desligar</button>
                        </div>
                        <div>
                            {data[0].withCup == true ? <span>Copo no lugar.</span> : <span>Por favor, coloque um copo.</span> }
                        </div>
                        <div>
                            <Typography id="discrete-slider" gutterBottom>
                                Quantidade
                            </Typography>
                            <Slider
                                style={ {width:200} }
                                defaultValue={50}
                                getAriaValueText={valuetext}
                                aria-labelledby="discrete-slider"
                                valueLabelDisplay="auto"
                                step={10}
                                marks={marks}
                                min={10}
                                max={200}
                                valueLabelDisplay="on"
                            />
                        </div>
                        <div>
                                <button onClick={this.startPrepare}>Preparar café</button><CircularProgress variant="determinate" {...props} />
                        </div>
                    </div> : 
                    <div>
                        <span>Cafeteira Desligada</span>
                        <button onClick={this.turnOn}>Ligar</button>
                    </div>
                }
            </div>
        );
    }
};
