import React, { Component } from 'react';
import { firebaseDatabase } from '../utils/firebaseUtils';

var activeMachine = false;

export default class System extends Component {
    updateActive = (valor) => {
        activeMachine = valor;
    }

    render() {
        var db = firebaseDatabase.ref('active');
        db.on('value', snap => { const data = snap.val(); this.updateActive(data) })
        return (
            <div>
                <span>Cafeteira {activeMachine ? 'Ligada' : 'Desligada'}</span>
            </div>
        );
    }
};
