    turnOff = () => {
        this.setState({ active: false })

        firebaseDatabase.ref().update({
            active: false
        })
    }

    render() {
        let { active } = this.state;
        return (
            <div>
                { 
                    active ? <div>
                        <span>Cafeteira Ligada</span>
                        <button onClick={this.turnOff}>Desligar</button>
                    </div> : <div>
                        <span>Cafeteira Desligada</span>
                        <button onClick={this.turnOn}>Ligar</button>
                    </div>
                }
            </div>
        );
    }
};
