import React, { Component } from 'react';
import { firebaseDatabase } from '../utils/firebaseUtils';

export default class System extends Component {

    constructor(props) {
        super(props);
        this.state = this.initialState();
    }

    initialState = () => {
        return {
            active: false
        }
    };

    async activeMachine() {
        let value = 'Desligado'
        await firebaseDatabase.ref('active').on('value', snap => {
           value = snap.val();
        })
        return value;
    };

    render() {
        return (
            <div>
                <span>Status da cafeteira: {this.activeMachine()}</span>
            </div>
        );
    }
};
