import React, { Component } from 'react';
import { firebaseDatabase } from '../utils/firebaseUtils';



export default class System extends Component {
    activeMachine = () => {
        firebaseDatabase.ref('active').on('value', snap => {
            console.log(snap.val() == true)
            if (snap.val() == true) {
                return 'Ligada';
            }
            return 'Desligada';
        })
    }

    render() {
        return (
            <div>
                {console.log(activeMachine())}
                <span>Status da cafeteira: {activeMachine()}</span>
            </div>
        );
    }
};
