import React, { Component } from 'react';
import { firebaseDatabase } from '../utils/firebaseUtils';

var data;

firebaseDatabase.ref().on('value', snap => {
   data = snap.val(); 
});


export default class System extends Component {

    activeMachine = () => {
        firebaseDatabase.ref('active').on('value', snap => {
            return snap.val();
        })
    }

    render() {
        return (
            <div>
                <span>Cafeteira {this.activeMachine() ? 'Ligada' : 'Desligada'}</span>
            </div>
        );
    }
};
