import React, { Component } from 'react';
import { firebaseDatabase } from '../utils/firebaseUtils';

export default class System extends Component {

    constructor(props) {
        super(props);
        this.state = this.initialState();
    }

    initialState = () => {
        return {
            active: false
        }
    };

    activeMachine = () => {
        firebaseDatabase.ref('active').on('value', snap => {
            var value = snap.val();
            console.log(value)
            return value;
        })
    };

    render() {
        return (
            <div>
                {console.log(this.activeMachine())}
                <span>Status da cafeteira: {this.activeMachine() == 1 ? 'Ligado' : 'Desligado'}</span>
            </div>
        );
    }
};
