import React, { Component } from 'react';
import { firebaseDatabase } from '../utils/firebaseUtils';

var data;

function renderData() {
    firebaseDatabase.ref().on('value', snap => {
        data = snap.val(); 
    });
}

export default class System extends Component {

    constructor(props) {
        super(props);
        this.state = this.initialState();
    }

    initialState = () => {
        renderData();
        return {
            active: data.active
        }
    }

    render() {
        let { active } = this.state;

        return (

            <div>
                {console.log("-----", active)}
                <span>Cafeteira</span>
            </div>
        );
    }
};
