import React, { Component } from 'react';
import { firebaseDatabase } from '../utils/firebaseUtils';

var activeCoffe = false;
export default class System extends Component {

    activeMachine() {
        firebaseDatabase.ref('active').on('value', snap => {
            activeCoffe = snap.val()
        })
    };

    render() {
        this.activeMachine();
        return (
            <div>
                <span>Status da cafeteira: {activeCoffe ? 'Ligado' : 'Desligado'} </span>
            </div>
        );
    }
};
