import React, { Component } from 'react';
import { firebaseDatabase } from '../utils/firebaseUtils';

export default class System extends Component {

    constructor(props) {
        super(props);
        this.state = this.initialState();
    }

    initialState = () => {
        return {
            active: false
        }
    };

    activeMachine = () => {
        firebaseDatabase.ref('active').on('value', snap => {
            console.log(snap.val())
           if (snap.val()) {
               return 'Ligado'
           }
           return 'Desligado'
        })
    };

    render() {
        return (
            <div>
                <span>Status da cafeteira: {this.activeMachine()}</span>
            </div>
        );
    }
};
