import React, { Component } from 'react';
import { firebaseDatabase } from '../utils/firebaseUtils';
import { makeStyles } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';
import Slider from '@material-ui/core/Slider';

function valuetext(value) {
    return `${value}Â°C`;
}

const classes = useStyles();

export default class System extends Component {
    constructor(props) {
        super(props);
        this.state = this.initialState();
    }

    initialState = () => {
        return {
            data: []
        }
    }

    componentDidMount() {
        const dataRef = firebaseDatabase.ref();
        dataRef.on('value', (snapshot) => {
            let data = snapshot.val();
            let newState = [];

            newState.push({
                active: data.active, 
                withCup: data.withCup
            })

            this.setState({
                data: newState,
            })
        })
    }

    turnOn = () => {
        firebaseDatabase.ref().update({
            active: true
        })
    }

    turnOff = () => {
        firebaseDatabase.ref().update({
            active: false
        })
    }

    render() {
        let { data } = this.state;
        return (
            <div>
                { 
                    data[0] && (data[0].active == true) ? 
                    <div>
                        <div>
                            <span>Cafeteira Ligada</span>
                            <button onClick={this.turnOff}>Desligar</button>
                        </div>
                        <div>
                            {data[0].withCup == true ? <span>Copo no lugar.</span> : <span>Por favor, coloque um copo.</span> }
                        </div>
                        <div className={classes.root}>
                            <Typography id="discrete-slider" gutterBottom>
                                Quantidade
                            </Typography>
                            <Slider
                                defaultValue={50}
                                getAriaValueText={valuetext}
                                aria-labelledby="discrete-slider"
                                valueLabelDisplay="auto"
                                step={10}
                                marks
                                min={10}
                                max={200}
                            />
                        </div>
                    </div> : 
                    <div>
                        <span>Cafeteira Desligada</span>
                        <button onClick={this.turnOn}>Ligar</button>
                    </div>
                }
            </div>
        );
    }
};
