import React, { Component } from 'react';
import { firebaseDatabase } from '../utils/firebaseUtils';

function active() {
    firebaseDatabase.ref('active').on('value', snap => {
        console.log(snap.val() == true)
        if (snap.val() == true) {
            return 'Ligada';
        }
        return 'Desligada';
    })
}

export default class System extends Component {
    constructor(props) {
        super(props);
        this.state = this.initialState();
    }
    render() {
        return (
            <div>
                {console.log(active())}
                <span>Status da cafeteira: {active()}</span>
            </div>
        );
    }
};
