import React, { Component } from 'react';
import { firebaseDatabase } from '../utils/firebaseUtils';

export default class System extends Component {
    constructor(props) {
        super(props);
        this.state = this.initialState();
    }

    initialState = () => {
        return {
            active: false
        }
    }

    renderData = () => {
        var active = firebaseDatabase.ref('active');
        active.on('value', snap => { const data = snap.val(); this.setState({ active: data }) })
    }

    render() {
        let { active } = this.state;
        return (
            <div>
                {this.renderData()}
                <span>Cafeteira {active ? 'Ligada' : 'Desligada'}</span>
            </div>
        );
    }
};
