import React, { Component } from 'react';
import { firebaseDatabase } from '../utils/firebaseUtils';

export default class System extends Component {

    updateActive = (valor) => {
        console.log(valor)
    }

    render() {
        var active = firebaseDatabase.ref('active');
        var activeMachine = active.once('value', snap => { const data = snap.val(); updateActive(data) })
        return (
            <div>
                {console.log("-----", activeMachine)}
                <span>Cafeteira</span>
            </div>
        );
    }
};
